'use strict';

// setup example options

const data = { target: '/topic/a.b.c', payload: new Buffer.allocUnsafe(50).fill('X'), maxCount: 100000, logCount: 10000 };
const options = Object.assign({
    // tls : { host : '127.0.0.1', port: 5671 }
    // net : { host : '127.0.0.1', port: 5672 }
    // wss : { host : 'localhost', port: 433, path: '/' }
    // ws  : { host : 'localhost', port: 80, path: '/' }
    // sasl: { user: 'guest', password: 'guest' },
}, (process.argv.length > 2) ? require(process.argv[2]) : {}); options.data = Object.assign(data, options.data);

const { Client } = require('../index');
const { ForwardStatistics } = require('./tools');
const stats = new ForwardStatistics(options.data.maxCount, options.data.logCount);

// run usual application logic

const client = new Client(options);
const incoming = client.receiver('in').attach(options.data.echoSource ? options.data.echoSource : options.data.source, '');
const outgoing = client.sender('out').attach(options.data.echoTarget ? options.data.echoTarget : options.data.target, '', options.data.maxCount);

incoming.pipe(stats).pipe(outgoing);

stats
    .on('info', (count) => {
        console.log(count);
    })
    .on('done', () => {
        incoming.receiver().detach();
    })
;

incoming
    .on('subscribed', () => {
        console.log('subscribed');
    })
    .on('close', () => {
        client.disconnect();
    })
;

outgoing
    .on('ready', () => {
        console.log('ready');
    })
;

client
    .on('connected',(destination, peerInfo) => {
        console.log('connected', peerInfo.description);
    })
    .on('assert', (error) => {
        console.log(error.message);
    })
    .on('error', (error) => {
        console.log(error.message);
    })
    .on('reconnecting', (destination) => {
        console.log('reconnecting, using destination ' + destination);
    })
    .on('disconnected', (hadError, byBroker, statistics) => {
        console.log('disconnected');
        stats.print(statistics);
    })
;

client.connect();
